package BEAN;

public class answeruser {
private int num;
private String answer;
public answeruser() {
	// TODO Auto-generated constructor stub
}
public answeruser(int num, String answer) {
	super();
	this.num = num;
	this.answer = answer;
}
public int getNum() {
	return num;
}
public void setNum(int num) {
	this.num = num;
}
public String getAnswer() {
	return answer;
}
public void setAnswer(String answer) {
	this.answer = answer;
}

}
